set foreign_key_checks=1;



	`ProductId`,
	`ProductName`,
	`ShopId`,
	`ShopName`,
	`BrandId`,
	`BrandName`,
	`FirstCateId`,
	`FirstCateName`,
	`SecondCateId`,
	`SecondCateName`,
	`ThirdCateId`,
	`ThirdCateName`,
	`AttrValues`,
	`Comments`,
	`SaleCount`,
	`SalePrice`,
	`OnSaleTime`,
	`ImagePath`,
	`CanSearch`,
	`BrandLogo`
) SELECT
	a.Id,
	a.ProductName,
	a.ShopId,
	b.ShopName,
	a.BrandId,
	c.`Name` AS BrandName,
	SUBSTRING_INDEX(a.CategoryPath, '|', 1) AS FirstCateId,
	d. NAME AS FirstCateName,
	SUBSTRING_INDEX(
		SUBSTRING_INDEX(CategoryPath, '|', 2),
		'|',
		- 1
	) AS SecondCateId,
	e. NAME AS SecondCateName,
	SUBSTRING_INDEX(CategoryPath, '|', - 1) AS ThirdCateId,
	f. NAME AS ThirdCateName,
	g.AttrValues,
	0,
	0,
	a.MinSalePrice,
	a.AddedDate,
	a.ImagePath,
	(
		CASE
		WHEN a.SaleStatus = 1
		AND a.AuditStatus = 2
		AND a.IsDeleted = 0 THEN
			1
		ELSE
			0
		END
	) AS CanSearch,
	c.Logo
FROM
	himall_products a
LEFT JOIN himall_shops b ON a.ShopId = b.Id
LEFT JOIN himall_brands c ON a.BrandId = c.Id
LEFT JOIN himall_categories d ON SUBSTRING_INDEX(a.CategoryPath, '|', 1) = d.Id
LEFT JOIN himall_categories e ON SUBSTRING_INDEX(
	SUBSTRING_INDEX(a.CategoryPath, '|', 2),
	'|',
	- 1
) = e.Id
LEFT JOIN himall_categories f ON SUBSTRING_INDEX(a.CategoryPath, '|', - 1) = f.Id
LEFT JOIN (
	SELECT
		ProductId,
		group_concat(ValueId) AS AttrValues
	FROM
		himall_productattributes
	GROUP BY
		productId
) g ON a.Id = g.ProductId
WHERE
	a.Id not in (select ProductId from himall_searchproducts)
